#include <iostream>
#include <unistd.h>

using namespace std;

// Constante que vai para a área de text.
const string text = "";

// matriz que vai para o BSS não iniciado
long bss[1024][1024];

int main()
{ 
    long stack[100][1024];  // O que acontece se mudarmos de 100 para 1024?

    int heapElements = 10 * 1024 * 1024;
    long *heap = new long[heapElements];

    cout << "PID: " << getpid() << endl;
    cout << "Tamanho adicionado ao TEXT é: " << text.length() << " bytes" << endl;
    cout << "Tamanho adicionado ao BSS é: " << sizeof(bss) << " bytes" << endl;    
    cout << "Tamanho adicionado ao STACK é: " << sizeof(stack) << " bytes" << endl;
    cout << "Tamanho adicionado ao HEAP é: " << sizeof(long) * heapElements << " bytes" << endl;
    
    // Aguarda para encerrar programa.
    cin.ignore();
}